public class SubstringwithConcatenationofAllWords {
    public List<Integer> findSubstring(String S, String[] L) {
        List<Integer> result = new ArrayList<Integer>();
        Map<Integer, List<String>> map = new HashMap<Integer, List<String>>();
        if (L.length == 0) {
            return result;
        }

        String a = L[0];
        int elen = a.length();
        int len = L.length;
        if (S.length() < elen) {
            return result;
        }
        Map<String, Integer> set = new HashMap<String, Integer>();
        for (int i = 0; i < len; i++) {
            if (!set.containsKey(L[i])) {
                set.put(L[i], 1);
            } else {
                set.put(L[i], set.get(L[i]) + 1);
            }
        }
        for (int i = elen; i <= S.length(); i++) {
            String cur = S.substring(i - elen, i);
            if (set.containsKey(cur)) {
                if (!map.containsKey(i - elen)) {
                    List<String> c = new ArrayList<String>();
                    c.add(cur);
                    map.put(i, c);
                    if (c.size() == len) {
                        result.add(i - (len) * elen);
                    }
                } else {
                    List<String> before = map.get(i - elen);
                    if (before.size() == len) {
                        before.remove(0);
                    }
                    int count = 0;
                    int id = -1;
                    for (int j = 0; j < before.size(); j++) {
                        if (before.get(j).equals(cur)) {
                            count++;
                        }
                        if(count==set.get(cur)) {
                            id = j;
                            for (int k = 0; k <= id; k++) {
                                before.remove(0);
                            }
                            break;
                        }
                    }
                    before.add(cur);
                    if (before.size() == len) {
                        result.add(i - (len) * elen);
                    }
                    map.put(i, before);
                }
            }
        }
        return result;
    }

    public static void main(String[] args) {
        String S = "";
        String[] L = {"ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba"};
        long start = System.currentTimeMillis();
        List<Integer> integers = new SubstringwithConcatenationofAllWords().findSubstring(S, L);
        long end = System.currentTimeMillis();
        System.out.println(end-start);
        for (Integer i : integers) {
            System.out.print(i + " ");
        }
    }
}
